package spring.project.apirestbookstore.entity;import javax.persistence.*;import java.io.Serializable;import java.util.Date;@Entity@Table(name = "books")public class Book implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    public Long id;    public String title;    public String description;    public String author;    public String category;    public String language;    public Integer pages;    public Integer quantity;    public Double price;    @Column(name = "created_at")    @Temporal(TemporalType.DATE)    public Date createdAt;    public String status;}