package spring.project.apirestbookstore.implementation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import spring.project.apirestbookstore.entity.Book;import spring.project.apirestbookstore.repository.IBookRepository;import spring.project.apirestbookstore.service.IBookService;import java.util.Date;import java.util.List;@Servicepublic class BookServiceImpl implements IBookService {    @Autowired    private IBookRepository bookRepository;    @Override    public List<Book> select(String status) {        return bookRepository.findByStatus(status);    }    @Override    public Book get(Long id) {        return bookRepository.findById(id).orElse(null);    }    @Override    public Book insert(Book book) {        book.status = "ENABLED";        book.createdAt = new Date();        return bookRepository.save(book);    }    @Override    public Book update(Book book, Long id) {        Book dbBook = bookRepository.findById(id).orElse(null);        dbBook.title = book.title;        dbBook.description = book.description;        dbBook.author = book.author;        dbBook.category = book.category;        dbBook.language = book.language;        dbBook.pages = book.pages;        dbBook.quantity = book.quantity;        dbBook.price = book.price;        return bookRepository.save(dbBook);    }    @Override    public Book delete(Long id) {        Book dbBook = bookRepository.findById(id).orElse(null);        dbBook.status = "DISABLED";        return bookRepository.save(dbBook);    }}