package spring.project.apirestbookstore.implementation;import lombok.extern.java.Log;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import spring.project.apirestbookstore.model.entity.Book;import spring.project.apirestbookstore.repository.IBookRepository;import spring.project.apirestbookstore.service.IBookService;import java.util.Date;import java.util.List;@Log@Servicepublic class BookServiceImpl implements IBookService {    @Autowired    private IBookRepository bookRepository;    @Override    public List<Book> select(String status) {        return bookRepository.findByStatus(status);    }    @Override    public Book get(Long id) {        return bookRepository.findById(id).orElse(null);    }    @Override    public Book insert(Book book) {        book.setStatus("ENABLED");        book.setCreatedAt(new Date());        return bookRepository.save(book);    }    @Override    public Book update(Book book, Long id) {        Book dbBook = bookRepository.findById(id).orElse(null);        if (dbBook == null) {            log.warning("dbBook is null");            return null;        }        dbBook.setTitle(book.getTitle());        dbBook.setDescription(book.getDescription());        dbBook.setAuthor(book.getAuthor());        dbBook.setCategory(book.getCategory());        dbBook.setLanguage(book.getLanguage());        dbBook.setPages(book.getPages());        dbBook.setQuantity(book.getQuantity());        dbBook.setPrice(book.getPrice());        return bookRepository.save(dbBook);    }    @Override    public Book delete(Long id) {        Book dbBook = bookRepository.findById(id).orElse(null);        if (dbBook == null) {            log.warning("dbBook is null");            return null;        }        dbBook.setStatus("DISABLED");        return bookRepository.save(dbBook);    }}