package spring.project.apirestbookstore.model.entity;import lombok.AllArgsConstructor;import lombok.Builder;import lombok.Data;import lombok.NoArgsConstructor;import javax.persistence.*;@Entity@Table(name = "authors")@Data@NoArgsConstructor@AllArgsConstructor@Builderpublic class Author {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    private Long id;    @Column(name = "name")    private String name;}