package spring.project.msdetailbookstore.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.*;import spring.project.msdetailbookstore.entity.Book;import java.util.List;@FeignClient(name = "service-bookstore")public interface IBookClient {    @GetMapping("/select")    List<Book> select();    @GetMapping("/get/{id}")    Book get(@PathVariable Long id);    @PostMapping("/insert")    Book insert(@RequestBody Book book);    @PutMapping("/update/{id}")    Book update(@RequestBody Book book, @PathVariable Long id);    @DeleteMapping("/delete/{id}")    Book delete(@PathVariable Long id);}