package spring.project.msdetailbookstore.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import spring.project.msdetailbookstore.entity.Book;import java.util.List;@FeignClient(name = "service-bookstore")public interface IBookClient {    @GetMapping("/select")    List<Book> select();    @GetMapping("/get/{id}")    Book get(@PathVariable Long id);}