package spring.project.msdetailbookstore.implementation;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import spring.project.msdetailbookstore.client.IBookClient;import spring.project.msdetailbookstore.entity.Detail;import spring.project.msdetailbookstore.service.IDetailService;import java.util.List;import java.util.stream.Collectors;@Service("serviceFeign")public class DetailServiceImpl implements IDetailService {    @Autowired    private IBookClient bookClient;    @Override    public List<Detail> select() {        List<Detail> details = bookClient.select()                .stream()                .map(book -> new Detail(book))                .collect(Collectors.toList());        return details;    }    @Override    public Detail get(Long id) {        return new Detail(bookClient.get(id));    }}