package springproject.msuser.controller;import org.modelmapper.ModelMapper;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import springproject.msuser.model.dto.UserDto;import springproject.msuser.model.entity.User;import springproject.msuser.service.IUserService;import java.util.List;import java.util.stream.Collectors;@RestController@RequestMappingpublic class UserController {    @Autowired    private IUserService userService;    @Autowired    private ModelMapper modelMapper;    private UserDto convertToDto(User user) {        return modelMapper.map(user, UserDto.class);    }    private User convertToEntity(UserDto userDto) {        return modelMapper.map(userDto, User.class);    }    @GetMapping("/select")    public List<UserDto> select() {        List<User> users = userService.select(true);        return users.stream()                .map(this::convertToDto)                .collect(Collectors.toList());    }    @GetMapping("/get/{id}")    public UserDto get(@PathVariable Long id) {        return convertToDto(userService.get(id));    }    @PostMapping("/insert")    public UserDto insert(@RequestBody UserDto userDto) {        User dbUser = convertToEntity(userDto);        User create = userService.insert(dbUser);        return convertToDto(create);    }    @PutMapping("/update/{id}")    public UserDto update(@RequestBody UserDto userDto, @PathVariable Long id) {        User dbUser = convertToEntity(userDto);        User edit = userService.update(dbUser, id);        return convertToDto(edit);    }    @DeleteMapping("/delete/{id}")    public User delete(@PathVariable Long id) {        return userService.delete(id);    }    @GetMapping("/find")    public User find(@RequestParam String user) {        return userService.find(user);    }}