package springproject.msuser.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.*;import springproject.msuser.entity.User;import springproject.msuser.service.IUserService;import java.util.List;@RestController@RequestMappingpublic class UserController {    @Autowired    private IUserService userService;    @GetMapping("/select")    public List<User> select() {        return userService.select(true);    }    @GetMapping("/get/{id}")    public User get(@PathVariable Long id) {        return userService.get(id);    }    @PostMapping("/insert")    public User insert(@RequestBody User user) {        return userService.insert(user);    }    @PutMapping("/update/{id}")    public User update(@RequestBody User user, @PathVariable Long id) {        return userService.update(user, id);    }    @DeleteMapping("/delete/{id}")    public User delete(@PathVariable Long id) {        return userService.delete(id);    }    @GetMapping("/find")    public User find(@RequestParam String user) {        return userService.find(user);    }}