package springproject.serviceoauth.client;import org.springframework.cloud.openfeign.FeignClient;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestParam;import springproject.serviceoauth.entity.User;@FeignClient(name = "service-user")public interface IUserFeign {    @GetMapping("/find")    User find(@RequestParam String user);}