package springproject.serviceoauth.security;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.oauth2.common.DefaultOAuth2AccessToken;import org.springframework.security.oauth2.common.OAuth2AccessToken;import org.springframework.security.oauth2.provider.OAuth2Authentication;import org.springframework.security.oauth2.provider.token.TokenEnhancer;import org.springframework.stereotype.Component;import springproject.serviceoauth.entity.User;import springproject.serviceoauth.service.IUserService;import java.util.HashMap;import java.util.Map;@Componentpublic class InfoToken implements TokenEnhancer {    @Autowired    private IUserService userService;    @Override    public OAuth2AccessToken enhance(OAuth2AccessToken accessToken, OAuth2Authentication authentication) {        Map<String, Object> value = new HashMap<>();        User user = userService.findByUsername(authentication.getName());        value.put("name", user.getName());        value.put("email", user.getEmail());        ((DefaultOAuth2AccessToken) accessToken).setAdditionalInformation(value);        return accessToken;    }}